(function() {
  var Random, U, rand, standardUniformDistribution, τ;

  τ = 2 * Math.PI;

  U = function(min, max) {
    return function() {
      return Math.random() * (max - min) + min;
    };
  };

  standardUniformDistribution = U(0, 1);

  rand = function(n) {
    if (n) {
      return Math.floor(n * standardUniformDistribution());
    } else {
      return standardUniformDistribution();
    }
  };

  module.exports = Random = {
    angle: function() {
      return rand() * τ;
    },
    binomial: function(n, p) {
      var _i, _results;
      if (n == null) {
        n = 1;
      }
      if (p == null) {
        p = 0.5;
      }
      return (function() {
        _results = [];
        for (var _i = 0; 0 <= n ? _i < n : _i > n; 0 <= n ? _i++ : _i--){ _results.push(_i); }
        return _results;
      }).apply(this).map(function() {
        if (rand() < p) {
          return 1;
        } else {
          return 0;
        }
      }).reduce(function(a, b) {
        return a + b;
      }, 0);
    },
    between: function(min, max) {
      return rand() * (max - min) + min;
    },
    rand: rand,
    signed: function(n) {
      if (n == null) {
        n = 1;
      }
      return (n * rand()) - (n / 2);
    }
  };

}).call(this);

